import withLayout from '../HOC/withLayout'
import Link from 'next/link';
// import style from 'next/css'

// import "../sass/main.scss"
import "../css/style.css"

// const mainStyle = style({ background: 'url(/static/bg.png) no-repeat center center scroll' })

const PostLink = props => (
  <li>
    <Link href={`/post?title=${props.title}`}>
      <a>{props.title}</a>
    </Link>
  </li>
);
const Blog = () => {
  return (
    <>
    <style jsx>{`
        .header {
          height: 95vh;
          background-image: linear-gradient(
            rgba(30, 144, 255, 0.5),
            rgba(0, 116, 228, 0.5)
            ),
            url(../../static/mountain2.jpg);
          background-repeat: repeat-x; 
          background-size: cover;
          background-position: top;
          position: relative;
      }
      `}</style>
      <main>
        <div className="container">
          <header className="header">
            <div className="header__text-box">
              <h1 className="heading-primary">
                <span className="heading-primary--main">Craig MacRitchie</span>
                <div className="heading-primary-container">
                  <span className="heading-primary--sub">Full-Stack Developer</span>
                  <span className="heading-primary--sub">Data Scientist</span>
                  <span className="heading-primary--sub">Engineer in Training</span>
                </div>
              </h1>
            </div>
          </header>
        </div>
      </main>
    </>
  );
}

export default withLayout(Blog)
