import { useState, useRef, useEffect } from 'react'
import Link from 'next/link';

const DynamicNavigation = ({ isSticky, pageDetails: { back, next, title } }) => {
    return (
        <>
        <div className="nav_sticky navigation-links">
        <Link className="navigation-links__button" href={back} passHref> 
            Back
        </Link>
            
            <div className={`navigation-links__page ${!isSticky ? 'disappear' : 'appear'}`}>{title}</div>
        <Link className="navigation-links__button" href={next} passHref> 
            {next}
        </Link>
        </div>
        </>
    )
}

export default DynamicNavigation
