// import withLayout from '../HOC/withLayout'
import FadeInSection from '../HOC/fadeInSection'
import Link from 'next/link';
import dynamic from 'next/dynamic';
// import style from 'next/css'

// import "../sass/main.scss"
import "../css/style.css"

// import Frameworks from 
// import Projects from "./projects"
const DynamicAbout = dynamic(() => import("./about"))
const DynamicFrameworks = dynamic(() => import("./frameworks"))
const DynamicProjects = dynamic(() => import("./projects"))



// const mainStyle = style({ background: 'url(/static/bg.png) no-repeat center center scroll' })

const Home = () => {
  return (

    
      <>
      <style jsx>{`
      .header {
        height: 90vh;
        background-image: linear-gradient(
          rgba(30, 144, 255, 0.5),
          rgba(0, 116, 228, 0.5)
          ),
          url(../../static/mountain2.jpg);
        background-repeat: repeat-x; 
        background-size: cover;
        background-position: left;
        position: relative; 

      }
    }

    @media screen and (max-width: 700px) {
      .header{
        background-position: center;
          background-image: linear-gradient(
              rgba(30, 144, 255, 0.5),
              rgba(0, 116, 228, 0.5)
              )
      }
    }
    `}</style>
      <div className="container">
        <header className="header">
          <div className="header__text-box">
            <h1 className="heading-primary">
              <span className="heading-primary--main">Craig MacRitchie</span>
              <div className="heading-primary--container">
                <div className="heading-primary--sub heading-primary--sub--1">Full-Stack Developer</div>
                <div className="heading-primary--sub heading-primary--sub--2">Data Scientist</div>
                <div className="heading-primary--sub heading-primary--sub--3">Engineer in Training</div>
              </div>
            </h1> 
          </div>   
        </header>
        <div className="navigation-links">
          <Link href="/about">
            {/* <a className="btn btn--white btn--animated btn__header">About</a> */}
            <a className="navigation-links__location">about</a>
          </Link>
          <Link href="/projects">
            {/* <a className="btn btn--white btn--animated btn__header">Projects</a> */}
            <a className="navigation-links__location">projects</a>
          </Link>
          <Link href="/frameworks">
            {/* <a href="#section-tours" className="btn btn--white btn--animated btn__header">Frameworks</a> */}
            <a className="navigation-links__location">Skills</a>
          </Link>
          <Link href="/contact">
            {/* <a href="#section-tours" className="btn btn--white btn--animated btn__header">Contact</a>  */}
            <a className="navigation-links__location">contact</a>
          </Link> 
        </div>
          <FadeInSection>
            <DynamicAbout />
          </FadeInSection>
          <FadeInSection>
            <DynamicFrameworks />
          </FadeInSection>
          <FadeInSection>
            <DynamicProjects />
          </FadeInSection>
      </div>
    
  </>
  )
}

export default Home
